import React from 'react'

//destructuring props to include selected word and correct letters
function Word ({ selectedWord, correctLetters }) {
    return (
      <>
       <div className="word">
       ${selectedWord
			.split('')
			.map(
				letter => `
          <span class="letter">
            ${correctLetters.includes(letter) ? letter : ''}
          </span>
        `
			)
			.join('')}
  `;

       </div>
      </>
    );
}

export default Word
