// import logo from './logo.svg';
import React, { useState, useEffect} from 'react';
import Header from './components/Header'
import Figure from './components/Figure'
import WrongLetters from './components/WrongLetters';
import Word from './components/Word';
import Popup from './components/Popup';
import Notification from './components/Notification';
import './App.css';

const words = ['animal', 'code', 'animate', 'flavor'];

let selectedWord = words[Math.floor(Math.random() * words.length)];

function App() {
  //useState to keep track and see if the game is playable, initially set to true
  const [playable, setPlayable] = useState(true);
//useState to keep track of correct letters, initially set to empty array
  const [correctLetters, setCorrectLetters] = useState([]);
//useState to keep track of wrong letters, initially set to empty array
  const [wrongLetters, setWrongLetters] = useState([]);

  //useEffect meant to be a side effect of the app
useEffect(() => {
  const handleKeydown = event => {
    //destructure function
    const {key, keyCode} = event;
    //if the user inputs a letter key on the keyboard 
      if (playable && keyCode >= 65 && e.keyCode <= 90) {
      //get the letter user input
        const letter = e.key.toLowerCase();

        if (selectedWord.includes(letter)) {
          if (!correctLetters.includes(letter)) {
            correctLetters.push(letter);

            displayWord();
          } else {
            showNotification();
          }
        } else {
          if (!wrongLetters.includes(letter)) {
            wrongLetters.push(letter);

            updateWrongLettersEl();
          } else {
            showNotification();
          }
        }
      }
    }
});
  // Keydown letter press
window.addEventListener('keydown', e => {

});


  return (
    <>
      <Header />
      <div className="game-container">
        <Figure />
        <WrongLetters />
        {/* passing selectedWord and correctedLetters props in word component*/}
        <Word selectedWord={selectedWord} correctLetters={correctLetters} />
      </div>
      <Popup />
      <Notification />
    </> 
  );
}

export default App;
