import React, { useEffect} from 'react'
import {checkWin} from '../helpers/helpers'

const Popup = ({correctLetters, wrongLetters, selectedWord, setPlayable}) => {
   let finalMessage = ' ';
   let finalMessageRevealWord = '';
   let playable = true;
//passing props to check if we win or lose
   if (checkWin(correctLetters, wrongLetters, selectedWord) === 'win' ){
      finalMessage = 'Congratulations! You won!'
      //still playable if checkwin comes back blank, means we did not win or lose
      playable = false;
   } else if (checkWin(correctLetters, wrongLetters, selectedWord) === 'lose'){
      finalMessage = "You lost."
      finalMessageRevealWord = `The word was: ${selectedWord}`;
      playable = false;
   }
//set playable is a side effect so we use useEffect
//like this so everytime we add a letter in hangman, we want playable to run
useEffect(() => setPlayable(playable));

    return (
     <>
     {/* Container for final message */}
     {/* if final message does not equal to blank, then display flex of. If it is blank, display nothing */}
      <div className="popup-container" style={finalMessage !== '' ? {display: 'flex'} : {}}>
         <div className="popup">
          <h2>{finalMessage}</h2>
           <h3>{finalMessageRevealWord}</h3>
           <button>Play Again</button>
        </div>
        </div>
     </>
    )
}

export default Popup
